&lt;figure class=&#34;highlight yaml&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;code class=&#34;hljs yaml&#34;&gt;&lt;span class=&#34;hljs-attr&#34;&gt;feed:&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-attr&#34;&gt;limit:&lt;/span&gt; &lt;span class=&#34;hljs-number&#34;&gt;20&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-attr&#34;&gt;order_by:&lt;/span&gt; &lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;-date&amp;quot;&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-attr&#34;&gt;tag_dir:&lt;/span&gt; &lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;tag&amp;quot;&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-attr&#34;&gt;category_dir:&lt;/span&gt; &lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;category&amp;quot;&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-attr&#34;&gt;rss:&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-attr&#34;&gt;enable:&lt;/span&gt; &lt;span class=&#34;hljs-literal&#34;&gt;true&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-attr&#34;&gt;template:&lt;/span&gt; &lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;themes/layout/template/rss.ejs&amp;quot;&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-attr&#34;&gt;output:&lt;/span&gt; &lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;rss.xml&amp;quot;&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-attr&#34;&gt;atom:&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-attr&#34;&gt;enable:&lt;/span&gt; &lt;span class=&#34;hljs-literal&#34;&gt;true&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-attr&#34;&gt;template:&lt;/span&gt; &lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;themes/layout/template/atom.ejs&amp;quot;&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-attr&#34;&gt;output:&lt;/span&gt; &lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;atom.xml&amp;quot;&lt;/span&gt;&lt;br&gt;    &lt;span class=&#34;hljs-attr&#34;&gt;jsonFeed:&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-